import React, { useState } from 'react';
import {
  AppBar,
  Box,
  CssBaseline,
  Drawer,
  IconButton,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Toolbar,
  Typography,
  Avatar,
  Menu,
  MenuItem,
  Divider,
  Badge
} from '@mui/material';
import {
  Menu as MenuIcon,
  Dashboard as DashboardIcon,
  People as PeopleIcon,
  CalendarToday as CalendarIcon,
  LocalHospital as HospitalIcon,
  AdminPanelSettings as AdminIcon,
  PersonAdd as PersonAddIcon,
  Schedule as ScheduleIcon,
  ExitToApp as ExitIcon,
  AccountCircle as AccountIcon
} from '@mui/icons-material';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const drawerWidth = 240;

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuth();

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const handleProfileMenuOpen = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleProfileMenuClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    logout();
    handleProfileMenuClose();
    navigate('/login');
  };

  const getMenuItems = () => {
    const commonItems = [
      { text: 'Dashboard', icon: <DashboardIcon />, path: '/' },
      { text: 'Pacientes', icon: <PeopleIcon />, path: '/pacientes' },
    ];

    const roleSpecificItems = [];

    switch (user?.tipo) {
      case 'administrador':
        roleSpecificItems.push(
          { text: 'Funcionários', icon: <PersonAddIcon />, path: '/admin/funcionarios' },
          { text: 'Consultas', icon: <CalendarIcon />, path: '/consultas' }
        );
        break;

      case 'medico':
        roleSpecificItems.push(
          { text: 'Minha Agenda', icon: <ScheduleIcon />, path: '/medico/agenda' },
          { text: 'Consultas', icon: <CalendarIcon />, path: '/consultas' }
        );
        break;

      case 'atendente':
        roleSpecificItems.push(
          { text: 'Consultas', icon: <CalendarIcon />, path: '/atendente/consultas' }
        );
        break;

      case 'enfermeiro':
        roleSpecificItems.push(
          { text: 'Triagem', icon: <HospitalIcon />, path: '/enfermeiro/triagem' }
        );
        break;

      case 'farmaceutico':
        roleSpecificItems.push(
          { text: 'Farmácia', icon: <HospitalIcon />, path: '/farmacia' }
        );
        break;
    }

    return [...commonItems, ...roleSpecificItems];
  };

  const menuItems = getMenuItems();

  const drawer = (
    <div>
      <Toolbar>
        <Box display="flex" alignItems="center" width="100%">
          <HospitalIcon color="primary" sx={{ mr: 1 }} />
          <Typography variant="h6" noWrap component="div" color="primary">
            Clínica Médica
          </Typography>
        </Box>
      </Toolbar>
      <Divider />
      <List>
        {menuItems.map((item) => (
          <ListItem key={item.text} disablePadding>
            <ListItemButton
              selected={location.pathname === item.path}
              onClick={() => navigate(item.path)}
            >
              <ListItemIcon>
                {item.icon}
              </ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </div>
  );

  const getRoleColor = (tipo: string) => {
    const colors: { [key: string]: string } = {
      'administrador': '#f44336',
      'medico': '#2196f3',
      'enfermeiro': '#4caf50',
      'atendente': '#ff9800',
      'farmaceutico': '#9c27b0'
    };
    return colors[tipo] || '#757575';
  };

  const getRoleLabel = (tipo: string) => {
    const labels: { [key: string]: string } = {
      'administrador': 'Admin',
      'medico': 'Médico',
      'enfermeiro': 'Enfermeiro',
      'atendente': 'Atendente',
      'farmaceutico': 'Farmacêutico'
    };
    return labels[tipo] || tipo;
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar
        position="fixed"
        sx={{
          width: { sm: `calc(100% - ${drawerWidth}px)` },
          ml: { sm: `${drawerWidth}px` },
        }}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { sm: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap component="div" sx={{ flexGrow: 1 }}>
            Sistema de Gestão Hospitalar
          </Typography>

          {/* Indicador de role */}
          <Badge
            badgeContent={getRoleLabel(user?.tipo || '')}
            color="secondary"
            sx={{ mr: 2 }}
          >
            <Avatar
              sx={{ 
                bgcolor: getRoleColor(user?.tipo || ''),
                width: 32,
                height: 32,
                fontSize: '0.875rem'
              }}
            >
              {user?.nome?.charAt(0)?.toUpperCase()}
            </Avatar>
          </Badge>

          <IconButton
            size="large"
            edge="end"
            aria-label="account of current user"
            aria-controls="primary-search-account-menu"
            aria-haspopup="true"
            onClick={handleProfileMenuOpen}
            color="inherit"
          >
            <AccountIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Box
        component="nav"
        sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
        aria-label="mailbox folders"
      >
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true,
          }}
          sx={{
            display: { xs: 'block', sm: 'none' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
        >
          {drawer}
        </Drawer>
        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', sm: 'block' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
          open
        >
          {drawer}
        </Drawer>
      </Box>

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          width: { sm: `calc(100% - ${drawerWidth}px)` },
        }}
      >
        <Toolbar />
        {children}
      </Box>

      <Menu
        anchorEl={anchorEl}
        anchorOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        keepMounted
        transformOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        open={Boolean(anchorEl)}
        onClose={handleProfileMenuClose}
      >
        <MenuItem disabled>
          <Typography variant="body2" color="textSecondary">
            {user?.nome}
          </Typography>
        </MenuItem>
        <MenuItem disabled>
          <Typography variant="body2" color="textSecondary">
            {user?.email}
          </Typography>
        </MenuItem>
        <Divider />
        <MenuItem onClick={handleLogout}>
          <ListItemIcon>
            <ExitIcon fontSize="small" />
          </ListItemIcon>
          Sair
        </MenuItem>
      </Menu>
    </Box>
  );
};

export default Layout;
